package com.syntax.class01;

public class PrintorPrintln {

	public static void main(String[] args) {
		
		/*
		 * println ---Prints statement and creates new Line
		 * 
		 * print---just prints a statement and leaves cursor on same line 
		 * (no new line is added)
		 */
		
		
		// TODO Auto-generated method stub
		System.out.println("This is a println command");
		
		System.out.print("This Println command");
		
		System.out.println("Hello");
		
		System.out.println("Bye");
		
	}

}
