package com.syntax.class01;

public class ifStatementRecap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//without bracket at if condition, Java only execute first line and print others 
		int day=7;
		if(day==6) {
		System.out.println("Today is Saturday");
		System.out.println("We are having a class");
		}else {
		System.out.println("Today is not Saturday");
		System.out.println("Today is some other day");
		System.out.println("end of code");
	}

}
}
